const[detPhone,setDetails] =useState([]);

  // This effect depends on `props.projects`.
  // It will run on initial render and every time










  useEffect(() => {
    axios
      .get("http://localhost:5005/phones/")
      .then((response) => {

        setPhones([...response.data]);

      });
  }, []);



