import { useState } from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";

function AddBeer() {
  const [headline, setHeadline] = useState("");
  const [price, setPrice] = useState(1);
  const navigate = useNavigate();

  let beerName,tagline,description,first_brewed,brewers_tips,attenuation_level,contributed_by
  const handleSubmit = (e) => {
    // Prevent page reload on submit    
    e.preventDefault();
    // Create the body for the POST request    
    const body = { name: beerName, 
        tagline: tagline, 
        description:description,
        first_brewed:first_brewed,
        brewers_tips:brewers_tips,
        attenuation_level:attenuation_level,
        contributed_by:contributed_by
    };

    axios
      .post("https://ironbnb-m3.herokuapp.com/apartments", body)
      .then((response) => {
        // Reset the state
        setHeadline("");
        setPrice(1);

        // Navigate to the `/` page
        navigate('/');
      });
  };


  return (
    <div className="containeradd">
      <h3>Name</h3>
      <div>
      <form onSubmit={handleSubmit}>
        <label>Name</label>
        <div>
        <input
          type="text"
          name="Name"
          onChange={(e) => setHeadline(e.target.value)}
          value={beerName}
        />
</div>  
        <label>Tagline</label><div>
        <input
          type="text"
          name="pricePerDay"
          onChange={(e) => setPrice(e.target.value)}
          value={tagline}
        />
        </div>  <div>
        <label>Description</label>
        <input
          type="text"
          name="pricePerDay"
          onChange={(e) => setPrice(e.target.value)}
          value={description}
        />
        </div>  <div>
         <label>First_brewed</label>
        <input
          type="text"
          name="pricePerDay"
          onChange={(e) => setPrice(e.target.value)}
          value={first_brewed}
        /> 

        </div>  <div>
        <label>Brewers_tips</label>
        <input
          type="text"
          name="pricePerDay"
          onChange={(e) => setPrice(e.target.value)}
          value={brewers_tips}
        /> 
        </div>  <div>
         <label>Attenuation_level</label>
           <input
          type="number"
          name="pricePerDay"
          onChange={(e) => setPrice(e.target.value)}
          value={attenuation_level}
        />  
        </div>
       <div>
            <label>Contributed_by</label>
         <input
        type="text"
        name="pricePerDay"
        onChange={(e) => setPrice(e.target.value)}
        value={contributed_by}
      /><div>
        <button type="submit">Create Beer</button>
        </div>
        </div>
      </form>
      </div>
    </div>
  );
}

export default AddBeer;
